"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import apiService from "../services/api"
import Header from "../components/header"
import "./HistoryPage.css"

const HistoryPage = () => {
  const [history, setHistory] = useState([])
  const [isLoading, setIsLoading] = useState(true)
  const [error, setError] = useState("")
  const navigate = useNavigate()

  useEffect(() => {
    if (!apiService.isAuthenticated()) {
      navigate("/login")
      return
    }

    loadHistory()
  }, [navigate])

  const loadHistory = async () => {
    try {
      setIsLoading(true)
      const data = await apiService.getHistory()
      setHistory(data)
    } catch (error) {
      console.error("Error loading history:", error)
      setError("Kh√¥ng th·ªÉ t·∫£i l·ªãch s·ª≠")
    } finally {
      setIsLoading(false)
    }
  }

  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a m·ª•c n√†y?")) {
      return
    }

    try {
      await apiService.deleteHistory(id)
      setHistory(history.filter((item) => item.id !== id))
    } catch (error) {
      console.error("Error deleting history:", error)
      alert("Kh√¥ng th·ªÉ x√≥a m·ª•c n√†y")
    }
  }

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString("vi-VN")
  }

  if (isLoading) {
    return (
      <>
        <Header />
        <div className="history-container">
          <div className="loading">ƒêang t·∫£i l·ªãch s·ª≠...</div>
        </div>
      </>
    )
  }

  return (
    <>
      <Header />
      <div className="history-container">
        <h1>L·ªãch s·ª≠ t√≥m t·∫Øt</h1>

        {error && <div className="error-message">{error}</div>}

        {history.length === 0 ? (
          <div className="empty-history">
            <p>Ch∆∞a c√≥ l·ªãch s·ª≠ t√≥m t·∫Øt n√†o</p>
            <button onClick={() => navigate("/")} className="back-button">
              V·ªÅ trang ch·ªß
            </button>
          </div>
        ) : (
          <div className="history-list">
            {history.map((item) => (
              <div key={item.id} className="history-item">
                <div className="history-header">
                  <span className="history-date">{formatDate(item.created_at)}</span>
                  <button onClick={() => handleDelete(item.id)} className="delete-button">
                    üóëÔ∏è
                  </button>
                </div>

                <div className="history-content">
                  <div className="original-text">
                    <h4>VƒÉn b·∫£n g·ªëc:</h4>
                    <p>{item.original_text}</p>
